import { ref, reactive, computed, onMounted, onBeforeUnmount } from 'vue';
import { ElMessage, ElMessageBox } from 'element-plus';
import BaseEChart from '@/components/BaseEChart.vue';
import { Search, Refresh, View, Check, Download, Warning, ArrowUp, ArrowDown } from '@element-plus/icons-vue';

// 预警统计数据

export default {
  __name: 'WarningAnalysis',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const warningStats = reactive({
      total: 128,
      unhandled: 45,
      handled: 83,
      trend: 15
    });

    // 预警类型
    const warningTypes = [{
      label: '温度异常',
      value: 'temperature'
    }, {
      label: '压力异常',
      value: 'pressure'
    }, {
      label: '流量异常',
      value: 'flow'
    }, {
      label: '质量异常',
      value: 'quality'
    }];

    // 预警等级
    const warningLevels = [{
      label: '一般',
      value: 'low'
    }, {
      label: '重要',
      value: 'medium'
    }, {
      label: '严重',
      value: 'high'
    }, {
      label: '紧急',
      value: 'critical'
    }];

    // 日期快捷选项
    const dateShortcuts = [{
      text: '最近一天',
      value: () => {
        const end = new Date();
        const start = new Date();
        start.setTime(start.getTime() - 3600 * 1000 * 24);
        return [start, end];
      }
    }, {
      text: '最近一周',
      value: () => {
        const end = new Date();
        const start = new Date();
        start.setTime(start.getTime() - 3600 * 1000 * 24 * 7);
        return [start, end];
      }
    }, {
      text: '最近一月',
      value: () => {
        const end = new Date();
        const start = new Date();
        start.setTime(start.getTime() - 3600 * 1000 * 24 * 30);
        return [start, end];
      }
    }];

    // 筛选表单
    const filterForm = reactive({
      dateRange: [],
      warningType: '',
      warningLevel: ''
    });

    // 预警等级统计
    const warningLevelStats = [{
      label: '一般预警',
      count: 45,
      percentage: 35,
      type: 'info',
      color: '#909399',
      trend: 5
    }, {
      label: '重要预警',
      count: 38,
      percentage: 30,
      type: 'warning',
      color: '#E6A23C',
      trend: -2
    }, {
      label: '严重预警',
      count: 25,
      percentage: 20,
      type: 'danger',
      color: '#F56C6C',
      trend: 8
    }, {
      label: '紧急预警',
      count: 20,
      percentage: 15,
      type: 'error',
      color: '#ff0000',
      trend: -3
    }];

    // 图表相关
    const chartTimeRange = ref('day');
    const chartType = ref('stack');
    const chartOptions = computed(() => {
      const isStack = chartType.value === 'stack';
      return {
        tooltip: {
          trigger: 'axis',
          axisPointer: {
            type: 'shadow'
          }
        },
        legend: {
          data: ['一般', '重要', '严重', '紧急'],
          textStyle: {
            color: '#fff'
          }
        },
        grid: {
          left: '3%',
          right: '4%',
          bottom: '3%',
          containLabel: true
        },
        xAxis: {
          type: 'category',
          data: getTimeAxisData(),
          axisLine: {
            lineStyle: {
              color: '#4a9eff'
            }
          },
          axisLabel: {
            color: '#8cc8ff'
          }
        },
        yAxis: {
          type: 'value',
          axisLine: {
            lineStyle: {
              color: '#4a9eff'
            }
          },
          axisLabel: {
            color: '#8cc8ff'
          },
          splitLine: {
            lineStyle: {
              color: 'rgba(74, 158, 255, 0.2)'
            }
          }
        },
        series: [{
          name: '一般',
          type: isStack ? 'bar' : 'line',
          stack: isStack ? 'total' : undefined,
          smooth: !isStack,
          emphasis: {
            focus: 'series'
          },
          itemStyle: {
            color: '#909399'
          },
          data: generateRandomData(8, 10)
        }, {
          name: '重要',
          type: isStack ? 'bar' : 'line',
          stack: isStack ? 'total' : undefined,
          smooth: !isStack,
          emphasis: {
            focus: 'series'
          },
          itemStyle: {
            color: '#E6A23C'
          },
          data: generateRandomData(6, 8)
        }, {
          name: '严重',
          type: isStack ? 'bar' : 'line',
          stack: isStack ? 'total' : undefined,
          smooth: !isStack,
          emphasis: {
            focus: 'series'
          },
          itemStyle: {
            color: '#F56C6C'
          },
          data: generateRandomData(4, 6)
        }, {
          name: '紧急',
          type: isStack ? 'bar' : 'line',
          stack: isStack ? 'total' : undefined,
          smooth: !isStack,
          emphasis: {
            focus: 'series'
          },
          itemStyle: {
            color: '#ff0000'
          },
          data: generateRandomData(2, 4)
        }]
      };
    });

    // 预警列表数据
    const loading = ref(false);
    const currentPage = ref(1);
    const pageSize = ref(10);
    const total = ref(128);
    const warningList = ref([{
      time: '2024-03-20 10:30:00',
      type: '温度异常',
      level: '严重',
      description: '3号监测点温度超过阈值30%，当前温度85℃，已持续30分钟',
      status: '未处理',
      relatedData: [{
        parameter: '温度',
        value: '85℃',
        threshold: '65℃',
        deviation: '+30%'
      }, {
        parameter: '压力',
        value: '2.5MPa',
        threshold: '2.0MPa',
        deviation: '+25%'
      }]
    }, {
      time: '2024-03-20 09:15:00',
      type: '压力异常',
      level: '重要',
      description: '主管道压力波动异常，当前压力2.8MPa，波动范围±0.5MPa',
      status: '已处理',
      relatedData: [{
        parameter: '压力',
        value: '2.8MPa',
        threshold: '2.5MPa',
        deviation: '+12%'
      }, {
        parameter: '流量',
        value: '150L/s',
        threshold: '180L/s',
        deviation: '-17%'
      }],
      processInfo: {
        time: '2024-03-20 09:45:00',
        operator: '张工',
        solution: '调整减压阀开度，稳定主管道压力',
        result: '压力恢复正常，系统运行稳定'
      }
    }]);

    // 对话框相关
    const detailDialogVisible = ref(false);
    const processDialogVisible = ref(false);
    const currentWarning = ref(null);
    const processForm = reactive({
      solution: '',
      result: ''
    });

    // 工具函数
    const getTimeAxisData = () => {
      switch (chartTimeRange.value) {
        case 'day':
          return ['00:00', '03:00', '06:00', '09:00', '12:00', '15:00', '18:00', '21:00'];
        case 'week':
          return ['周一', '周二', '周三', '周四', '周五', '周六', '周日'];
        case 'month':
          return Array.from({
            length: 30
          }, (_, i) => `${i + 1}日`);
        default:
          return [];
      }
    };
    const generateRandomData = (min, max) => {
      return Array.from({
        length: getTimeAxisData().length
      }, () => Math.floor(Math.random() * (max - min + 1)) + min);
    };
    const getWarningTypeTag = type => {
      const typeMap = {
        '温度异常': 'danger',
        '压力异常': 'warning',
        '流量异常': 'info',
        '质量异常': 'error'
      };
      return typeMap[type] || 'info';
    };
    const getWarningLevelType = level => {
      const typeMap = {
        '一般': 'info',
        '重要': 'warning',
        '严重': 'danger',
        '紧急': 'error'
      };
      return typeMap[level] || 'info';
    };
    const getRowClassName = ({
      row
    }) => {
      if (row.status === '未处理') {
        return 'unhandled-warning';
      }
      return '';
    };

    // 方法
    const handleSearch = () => {
      loading.value = true;
      // 模拟API调用
      setTimeout(() => {
        loading.value = false;
        ElMessage.success('查询成功');
      }, 1000);
    };
    const handleReset = () => {
      Object.assign(filterForm, {
        dateRange: [],
        warningType: '',
        warningLevel: ''
      });
    };
    const handleSizeChange = val => {
      pageSize.value = val;
      // 重新加载数据
    };
    const handleCurrentChange = val => {
      currentPage.value = val;
      // 重新加载数据
    };
    const handleDetail = row => {
      currentWarning.value = row;
      detailDialogVisible.value = true;
    };
    const handleProcess = row => {
      currentWarning.value = row;
      processDialogVisible.value = true;
    };
    const submitProcess = () => {
      console.log('Process submitted:', processForm);
      processDialogVisible.value = false;
      // 更新预警状态
    };
    const exportData = () => {
      ElMessageBox.confirm('确认导出当前筛选条件下的预警数据？', '导出确认', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(() => {
        ElMessage.success('导出成功');
      });
    };
    const refreshList = () => {
      loading.value = true;
      setTimeout(() => {
        loading.value = false;
        ElMessage.success('数据已更新');
      }, 1000);
    };

    // 自动刷新
    let refreshTimer = null;
    const startAutoRefresh = () => {
      refreshTimer = setInterval(() => {
        if (!loading.value) {
          refreshList();
        }
      }, 60000); // 每分钟刷新一次
    };
    onMounted(() => {
      startAutoRefresh();
    });
    onBeforeUnmount(() => {
      if (refreshTimer) {
        clearInterval(refreshTimer);
      }
    });
    const __returned__ = {
      warningStats,
      warningTypes,
      warningLevels,
      dateShortcuts,
      filterForm,
      warningLevelStats,
      chartTimeRange,
      chartType,
      chartOptions,
      loading,
      currentPage,
      pageSize,
      total,
      warningList,
      detailDialogVisible,
      processDialogVisible,
      currentWarning,
      processForm,
      getTimeAxisData,
      generateRandomData,
      getWarningTypeTag,
      getWarningLevelType,
      getRowClassName,
      handleSearch,
      handleReset,
      handleSizeChange,
      handleCurrentChange,
      handleDetail,
      handleProcess,
      submitProcess,
      exportData,
      refreshList,
      get refreshTimer() {
        return refreshTimer;
      },
      set refreshTimer(v) {
        refreshTimer = v;
      },
      startAutoRefresh,
      ref,
      reactive,
      computed,
      onMounted,
      onBeforeUnmount,
      get ElMessage() {
        return ElMessage;
      },
      get ElMessageBox() {
        return ElMessageBox;
      },
      BaseEChart,
      get Search() {
        return Search;
      },
      get Refresh() {
        return Refresh;
      },
      get View() {
        return View;
      },
      get Check() {
        return Check;
      },
      get Download() {
        return Download;
      },
      get Warning() {
        return Warning;
      },
      get ArrowUp() {
        return ArrowUp;
      },
      get ArrowDown() {
        return ArrowDown;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
