    // 生成时间序列数据
    function generateTimeData(baseValue, variance) {
      const now = new Date();
      const data = [];
      for (let i = 0; i < 60; i++) {
        const time = new Date(now - (59 - i) * 60000);
        data.push([time.toISOString(), +(baseValue + (Math.random() * 2 - 1) * variance).toFixed(1)]);
      }
      return data;
    }

