import { createRouter, createWebHistory } from 'vue-router'
import AppLayout from '@/layouts/AppLayout.vue'

const routes = [
  {
    path: '/',
    component: AppLayout,
    children: [
      {
        path: '',
        name: 'SystemOverview',
        component: () => import('@/views/SystemOverview.vue'),
        meta: { title: '系统概览' }
      },
      {
        path: '/data',
        name: 'DataManagement',
        component: () => import('@/views/DataManagement.vue'),
        meta: { title: '数据管理' }
      },
      {
        path: '/quality',
        name: 'QualityEvaluation',
        component: () => import('@/views/QualityEvaluation.vue'),
        meta: { title: '质量评价' }
      },
      {
        path: '/analysis/farmland',
        name: 'FarmlandAnalysis',
        component: () => import('@/views/DataAnalysis/FarmlandAnalysis.vue'),
        meta: { title: '农田景观分析' }
      }
    ]
  },
  {
    path: '/login',
    name: 'Login',
    component: () => import('@/views/Login.vue'),
    meta: { title: '登录' }
  },
  {
    path: '/register',
    name: 'Register',
    component: () => import('@/views/Register.vue'),
    meta: { title: '注册' }
  },
  {
    path: '/:pathMatch(.*)*',
    name: 'NotFound',
    component: () => import('@/views/404.vue'),
    meta: { title: '404' }
  }
]

const router = createRouter({
  history: createWebHistory(),
  routes
})

router.beforeEach((to, from, next) => {
  // 设置页面标题
  document.title = to.meta.title ? `${to.meta.title} - 星地一体化智能土壤环境分析监测系统` : '星地一体化智能土壤环境分析监测系统'
  
  // 检查是否需要登录
  const publicPages = ['/login', '/register']
  const authRequired = !publicPages.includes(to.path)
  const loggedIn = localStorage.getItem('token')

  if (authRequired && !loggedIn) {
    return next('/login')
  }

  next()
})

export default router
