import { createRouter, createWebHistory } from 'vue-router'
import AppLayout from '@/layouts/AppLayout.vue'

const routes = [
  {
    path: '/',
    component: AppLayout,
    children: [
      {
        path: '',
        name: 'SystemOverview',
        component: () => import('@/views/SystemOverview.vue'),
        meta: { title: '系统概览' }
      },
      {
        path: '/data',
        name: 'DataManagement',
        component: () => import('@/views/DataManagement.vue'),
        meta: { title: '数据管理' }
      },
      {
        path: '/quality',
        name: 'QualityEvaluation',
        component: () => import('@/views/QualityEvaluation.vue'),
        meta: { title: '质量评价' }
      },
      {
        path: '/analysis/farmland',
        name: 'FarmlandAnalysis',
        component: () => import('@/views/DataAnalysis/FarmlandAnalysis.vue'),
        meta: { title: '农田景观分析' }
      },
      {
        path: '/system',
        name: 'SystemHome',
        component: () => import('@/views/SystemOverview.vue'),
        meta: { title: '系统首页' }
      },
      {
        path: '/monitoring',
        name: 'Monitoring',
        component: () => import('@/views/Monitoring.vue'),
        meta: { title: '智能物联' }
      },
      {
        path: '/analysis',
        name: 'Analysis',
        component: () => import('@/views/Analysis.vue'),
        meta: { title: '智能监测' }
      },
      {
        path: '/info',
        name: 'Info',
        component: () => import('@/views/Info.vue'),
        meta: { title: '信息概述' }
      }
    ]
  },
  {
    path: '/:pathMatch(.*)*',
    name: 'NotFound',
    component: () => import('@/views/404.vue'),
    meta: { title: '404' }
  }
]

const router = createRouter({
  history: createWebHistory(),
  routes
})

router.beforeEach((to, from, next) => {
  // 设置页面标题
  document.title = to.meta.title ? `${to.meta.title} - 星地一体化智能土壤环境分析监测系统` : '星地一体化智能土壤环境分析监测系统'
  
  // 直接允许访问所有页面，不需要登录验证
  next()
})

export default router
